tool.command:
@SET_ACCESSIONS@
#if $input.input_select == "file":
    acc='${input.file.name}' &&
    ln -s '${input.file}' "\$acc" &&
#end if

fasterq-dump "\$acc"
$adv.split
#if str( $adv.minlen ) != "":
    --min-read-len "$adv.minlen"
#end if
$adv.skip_technical
&&
mkdir -p output;
count=`ls *.fastq | wc -l` ;
echo "There are \$count fastq";
data=(\$(ls *.fastq));
if [ "\$count" -eq 1 ]; then
    mv "\${data[0]}" output/"\${acc}"_single.fastqsanger;
elif [ "$adv.split" = "--split-3" ]; then
    if [ -e "\${acc}".fastq ]; then
        mv "\${acc}".fastq output/"\${acc}"_single.fastqsanger;
    fi;
    mv "\${acc}"_1.fastq output/"\${acc}"_forward.fastqsanger;
    mv "\${acc}"_2.fastq output/"\${acc}"_reverse.fastqsanger;
elif [ "\$count" -eq 2 ]; then
    mv "\${data[0]}" output/\$(basename "\${data[0]}" .fastq)_forward.fastqsanger;
    mv "\${data[1]}" output/\$(basename "\${data[1]}" .fastq)_reverse.fastqsanger;
else
    for file in \${data[*]}; do
        mv "\$file" output/"\$file"sanger;
    done;
fi;
#if $input.input_select=="file_list":
    ) ; done

    ;
#elif  $input.input_select=="accession_number":
);
#end if
#if str($outputformat) != "fastqsanger":
for f in \$(ls output/*.fastqsanger); do
    #if str($outputformat) == "fastqsanger.gz":
        gzip \$f;
    #elif str($outputformat) == "fastqsanger.bz2":   
        bzip2 \$f;
    #end if
done
#end if
